changeSet.sql:
DO $$
DECLARE
  partition_count INTEGER := ${partitionsCount};
  i INTEGER;
BEGIN
  FOR i IN 1..(partition_count) LOOP
    EXECUTE format('CREATE TABLE ${prefix}HISTORY_DELETION_PART_%s PARTITION OF ${prefix}HISTORY_DELETION FOR VALUES IN (%s)', i, i);
  END LOOP;
END $$;

DECLARE @partitionCount INT = ${partitionsCount};
DECLARE @sql NVARCHAR(MAX);
DECLARE @i INT = 0;

SET @sql = N'CREATE PARTITION FUNCTION ${prefix}PF_HISTORY_DELETION (INT) AS RANGE LEFT FOR VALUES (';
WHILE @i < @partitionCount - 1
BEGIN
  SET @sql = @sql + CAST(@i AS NVARCHAR(10));
  IF @i < @partitionCount - 2
    SET @sql = @sql + N', ';
  SET @i = @i + 1;
END;
SET @sql = @sql + N')';
EXEC sp_executesql @sql;

SET @sql = N'CREATE PARTITION SCHEME ${prefix}PS_HISTORY_DELETION AS PARTITION ${prefix}PF_HISTORY_DELETION ALL TO ([PRIMARY])';
EXEC sp_executesql @sql;
