mapper.update:
UPDATE ${prefix}BATCH_OPERATION
SET STATE = #{state},
END_DATE = #{endDate}
WHERE BATCH_OPERATION_KEY = #{batchOperationKey}

UPDATE ${prefix}BATCH_OPERATION_ITEM
SET STATE = #{newState}
WHERE BATCH_OPERATION_KEY = #{batchOperationKey}
AND STATE = #{oldState}

UPDATE ${prefix}BATCH_OPERATION
SET OPERATIONS_TOTAL_COUNT = OPERATIONS_TOTAL_COUNT + #{operationsTotalCount}
WHERE BATCH_OPERATION_KEY = #{batchOperationKey}

UPDATE ${prefix}BATCH_OPERATION t
SET OPERATIONS_FAILED_COUNT = OPERATIONS_FAILED_COUNT + 1
WHERE BATCH_OPERATION_KEY = #{id}

UPDATE ${prefix}BATCH_OPERATION t
SET OPERATIONS_COMPLETED_COUNT = OPERATIONS_COMPLETED_COUNT + 1
WHERE BATCH_OPERATION_KEY = #{id}

mapper.select:
SELECT * FROM ( SELECT bof.BATCH_OPERATION_KEY, bof.STATE, bof.OPERATION_TYPE, bof.START_DATE, bof.END_DATE, bof.OPERATIONS_TOTAL_COUNT, bof.OPERATIONS_FAILED_COUNT, bof.OPERATIONS_COMPLETED_COUNT, boe.PARTITION_ID, boe.TYPE, boe.MESSAGE FROM ( SELECT * FROM ( SELECT bo.BATCH_OPERATION_KEY, bo.STATE, bo.OPERATION_TYPE, bo.START_DATE, bo.END_DATE, bo.OPERATIONS_TOTAL_COUNT, bo.OPERATIONS_FAILED_COUNT, bo.OPERATIONS_COMPLETED_COUNT FROM ${prefix}BATCH_OPERATION bo
) filtered
) bof LEFT JOIN ${prefix}BATCH_OPERATION_ERROR boe ON (boe.BATCH_OPERATION_KEY = bof.BATCH_OPERATION_KEY)
) sorted

mapper.update:
UPDATE ${prefix}BATCH_OPERATION
SET HISTORY_CLEANUP_DATE = #{historyCleanupDate, jdbcType=TIMESTAMP}
WHERE BATCH_OPERATION_KEY = #{batchOperationKey}

UPDATE ${prefix}BATCH_OPERATION_ITEM
SET HISTORY_CLEANUP_DATE = #{historyCleanupDate, jdbcType=TIMESTAMP}
WHERE BATCH_OPERATION_KEY = #{batchOperationKey}

mapper.sql:
DELETE
FROM ${prefix}${tableName}
WHERE rowid IN (SELECT rowid
                FROM ${prefix}${tableName}
                WHERE HISTORY_CLEANUP_DATE < #{cleanupDate}
                  AND ROWNUM <= #{limit})

DELETE
FROM ${prefix}${tableName}
WHERE ctid IN (SELECT ctid
               FROM ${prefix}${tableName}
               WHERE HISTORY_CLEANUP_DATE < #{cleanupDate}
  LIMIT #{limit})
