targets.expr:
histogram_quantile(0.95,
  sum by (le, grpc_method) (
    increase(grpc_server_handled_latency_seconds_bucket{
      camunda_region="$region",
      grpc_method=~"$grpc_method",
      grpc_method!~"ActivateJobs"
    }[$__rate_interval])
  )
)

sum by(uri, method) (
  rate(http_server_requests_seconds_count{
    camunda_region=~"$region",
    cluster=~"$cluster",
    container=~"$container",
    namespace=~"$namespace",
    pod=~"$pod",
    uri=~"$uri",
    uri!~"^/actuator/health(/.*)?$|^/actuator/prometheus$"
  }[$__rate_interval])
)

